//The most annoying part of coding but also the most important part of it.


var mouseEvent = "empty";
var last_position_of_x = 0;
var last_position_of_y = 0;
canvas = document.getElementById("myCanvas");
variablex = canvas.getContext("2d");
canvas.addEventListener("mousedown",mousedownf);
function mousedownf(e){
   mouseEvent = "mousedown";

}
canvas.addEventListener("mouseup",mouseupf);
function mouseupf(e){
   mouseEvent = "mouseup";
    
}
canvas.addEventListener("mouseleave",mouseleavef);
function mouseleavef(e){
   mouseEvent = "mouseleave";
    
}
canvas.addEventListener("mousemove",mousemovef);
function mousemovef(e){
  var currentx = e.clientX - canvas.offsetLeft;
  var currenty = e.clientY - canvas.offsetTop;
  console.log("mouseMoveStarted");
  if(mouseEvent == "mousedown"){
    variablex.beginPath();
    variablex.strokeStyle = "red";
    variablex.lineWidth = 3;
    variablex.moveTo(last_position_of_x,last_position_of_y);
    variablex.lineTo(currentx,currenty);
    variablex.stroke();
  }
  last_position_of_x = currentx;
  last_position_of_y = currenty;
}